var mysql = require('mysql');

var pool = mysql.createPool({
	host: "localhost",
	user: "root",
	password: "1234",
	database: "facebluff"
  });

function parsePass(pass){
	if (pass == undefined){
		return false;
	}
	
	if (pass.length < 8){
		return false;
	}
	
	return true;	
}

function getUsers(email, pass, callback){
	pool.getConnection(function(err, connection) {
        if (err) {
            callback("Error al obtener la conexion")
        } else {
			connection.query(
				"SELECT * FROM users where email = ?",
				[email],
				function(err, rows) {
				connection.release()
				if (err) {
					callback("Error al obtener los usuarios", null)
				} else {
					callback(null, rows)
				}
			})
		}
	})
}

module.exports = {
	getUsers: getUsers,
	parsePass: parsePass
}





