/*
	Desde el principio del string hasta el final solo aceptamos caracteres
	alfanumericos
*/
const expresionPass = /^\w+$/;

/*
	Al principio del string 1 o mas caracteres alfanumericos, una @, 
	1 o mas caracteres alfanumericos, un punto y 1 o mas caracteres alfanumericos.
*/
const expresionEmail = /^\w+(@){1}\w+(\.){1}\w+$/

function parsePass(pass){
	if (pass == undefined){
		return false;
	}
	
	if (pass.length < 8){
		return false;
	}

	if (!expresionPass.test(pass)){
		return false;
	}

	return true;	
}

function parseEmail(email){
	if (email == undefined){
		return false;
	}

	if (!expresionEmail.test(email)){
		return false;
	}

	return true;	
}

function parseName(name){
	if (name == undefined){
		return false;
	}

	if (name.length == 0){
		return false;
	}

	return true;
}

function parseUser(user){
	if (parseEmail(user.email) && parsePass(user.password) && parseName(user.name)){
		return true;
	}

	return false;
}
module.exports = {
	parseUser: parseUser,
	parsePass: parsePass
}